#
# opencascade
#

REPLACEME_SV_SPECIAL_COMPILER_SCRIPT

export CC=REPLACEME_CC
export CXX=REPLACEME_CXX

rm -Rf REPLACEME_SV_TOP_BIN_DIR_OPENCASCADE
mkdir -p REPLACEME_SV_TOP_BIN_DIR_OPENCASCADE
chmod u+w,a+rx REPLACEME_SV_TOP_BIN_DIR_OPENCASCADE

rm -Rf REPLACEME_SV_TOP_BLD_DIR_OPENCASCADE
mkdir -p REPLACEME_SV_TOP_BLD_DIR_OPENCASCADE
cd REPLACEME_SV_TOP_BLD_DIR_OPENCASCADE

REPLACEME_SV_CMAKE_CMD -G REPLACEME_SV_CMAKE_GENERATOR \
  -DCMAKE_BUILD_TYPE=REPLACEME_SV_CMAKE_BUILD_TYPE \
  -DUSE_VTK:BOOL=ON \
  -DBUILD_MODULE_Draw:BOOL=OFF \
  -D3RDPARTY_DIR:PATH=REPLACEME_SV_TOPLEVEL_BINDIR/REPLACEME_SV_COMPILER_BIN_DIR/REPLACEME_SV_ARCH_DIR \
  -D3RDPARTY_FREETYPE_DIR:PATH=REPLACEME_SV_TOP_BIN_DIR_FREETYPE \
  -D3RDPARTY_FREETYPE_DLL:FILEPATH=REPLACEME_SV_TOP_BIN_DIR_FREETYPE/lib/REPLACEME_SV_FREETYPE_LIBRARY \
  -D3RDPARTY_FREETYPE_DLL_DIR:PATH=REPLACEME_SV_TOP_BIN_DIR_FREETYPE/lib \
  -D3RDPARTY_FREETYPE_INCLUDE_DIR_freetype2:PATH=REPLACEME_SV_TOP_BIN_DIR_FREETYPE/include/freetype2 \
  -D3RDPARTY_FREETYPE_INCLUDE_DIR_ft2build:PATH=REPLACEME_SV_TOP_BIN_DIR_FREETYPE/include/freetype2 \
  -D3RDPARTY_FREETYPE_LIBRARY:FILEPATH=REPLACEME_SV_TOP_BIN_DIR_FREETYPE/lib/REPLACEME_SV_FREETYPE_LIBRARY \
  -D3RDPARTY_FREETYPE_LIBRARY_DIR:PATH=REPLACEME_SV_TOP_BIN_DIR_FREETYPE/lib \
  -D3RDPARTY_TCL_DIR:PATH=REPLACEME_SV_TOP_BIN_DIR_TCL \
  -D3RDPARTY_TCL_DLL:FILEPATH=REPLACEME_SV_TOP_BIN_DIR_TCL/REPLACEME_SV_TCL_DLL_DIR/REPLACEME_SV_TCL_DLL_NAME \
  -D3RDPARTY_TCL_DLL_DIR:PATH=REPLACEME_SV_TOP_BIN_DIR_TCL/REPLACEME_SV_TCL_DLL_DIR \
  -D3RDPARTY_TCL_INCLUDE_DIR:PATH=REPLACEME_SV_TOP_BIN_DIR_TCL/include \
  -D3RDPARTY_TCL_LIBRARY:FILEPATH=REPLACEME_SV_TOP_BIN_DIR_TCL/lib/REPLACEME_SV_TCL_LIB_NAME \
  -D3RDPARTY_TCL_LIBRARY_DIR:PATH=REPLACEME_SV_TOP_BIN_DIR_TCL/lib \
  -D3RDPARTY_TK_DIR:PATH=REPLACEME_SV_TOP_BIN_DIR_TK \
  -D3RDPARTY_TK_DLL:FILEPATH=REPLACEME_SV_TOP_BIN_DIR_TK/REPLACEME_SV_TK_DLL_DIR/REPLACEME_SV_TK_LIB_NAME \
  -D3RDPARTY_TK_DLL_DIR:PATH=REPLACEME_SV_TOP_BIN_DIR_TK/REPLACEME_SV_TK_DLL_DIR \
  -D3RDPARTY_TK_INCLUDE_DIR:PATH=REPLACEME_SV_TOP_BIN_DIR_TK/include \
  -D3RDPARTY_TK_LIBRARY:FILEPATH=REPLACEME_SV_TOP_BIN_DIR_TK/REPLACEME_SV_TK_LIB_NAME \
  -D3RDPARTY_TK_LIBRARY_DIR:PATH=REPLACEME_SV_TOP_BIN_DIR_TK/lib \
  -D3RDPARTY_VTK_DIR:PATH=REPLACEME_SV_TOP_BIN_DIR_VTK \
  -D3RDPARTY_VTK_DLL_DIR:PATH=REPLACEME_SV_TOP_BIN_DIR_VTK/bin \
  -D3RDPARTY_VTK_INCLUDE_DIR:PATH=REPLACEME_SV_TOP_BIN_DIR_VTK/include \
  -D3RDPARTY_VTK_LIBRARY_DIR:PATH=REPLACEME_SV_TOP_BIN_DIR_VTK/lib \
  -DQt5Core_DIR:PATH=REPLACEME_SV_QT5_DIR/lib/cmake/Qt5Core \
  -DQt5Gui_DIR:PATH=REPLACEME_SV_QT5_DIR/lib/cmake/Qt5Gui \
  -DQt5Network_DIR:PATH=REPLACEME_SV_QT5_DIR/lib/cmake/Qt5Network \
  -DQt5WebKit_DIR:PATH=REPLACEME_SV_QT5_DIR/lib/cmake/Qt5WebKit \
  -DINSTALL_DIR:PATH=REPLACEME_SV_TOP_BIN_DIR_OPENCASCADE \
  -DINSTALL_DIR_BIN=bin \
  -DINSTALL_DIR_LIB=lib \
REPLACEME_SV_TOP_SRC_DIR_OPENCASCADE

REPLACEME_SV_MAKE_CMD REPLACEME_SV_OPENCASCADE_MAKE_FILENAME REPLACEME_SV_MAKE_BUILD_PARAMETERS

REPLACEME_SV_MAKE_CMD REPLACEME_SV_OPENCASCADE_MAKE_FILENAME REPLACEME_SV_MAKE_INSTALL_PARAMETERS
