#
# freetype
#

REPLACEME_SV_SPECIAL_COMPILER_SCRIPT

export CC=REPLACEME_CC
export CXX=REPLACEME_CXX

rm -Rf REPLACEME_SV_TOP_BIN_DIR_FREETYPE
mkdir -p REPLACEME_SV_TOP_BIN_DIR_FREETYPE
chmod u+w,a+rx REPLACEME_SV_TOP_BIN_DIR_FREETYPE

rm -Rf REPLACEME_SV_TOP_BLD_DIR_FREETYPE
mkdir -p REPLACEME_SV_TOP_BLD_DIR_FREETYPE
cd REPLACEME_SV_TOP_BLD_DIR_FREETYPE

REPLACEME_SV_CMAKE_CMD -G REPLACEME_SV_CMAKE_GENERATOR \
   -DBUILD_SHARED_LIBS=REPLACEME_SV_FREETYPE_BUILD_SHARED \
   -DCMAKE_INSTALL_PREFIX=REPLACEME_SV_TOP_BIN_DIR_FREETYPE \
   -DCMAKE_BUILD_TYPE=REPLACEME_SV_CMAKE_BUILD_TYPE \
   -DBZIP2_LIBRARY_RELEASE="" \
   -DHARFBUZZ_LIBRARIES="" \
   -DPNG_LIBRARY_RELEASE="" \
   -DZLIB_LIBRARY_RELEASE="" \
REPLACEME_SV_TOP_SRC_DIR_FREETYPE

REPLACEME_SV_MAKE_CMD REPLACEME_SV_FREETYPE_MAKE_FILENAME REPLACEME_SV_MAKE_BUILD_PARAMETERS

REPLACEME_SV_MAKE_CMD REPLACEME_SV_FREETYPE_MAKE_FILENAME REPLACEME_SV_MAKE_INSTALL_PARAMETERS



