#
# gdcm
#

REPLACEME_SV_SPECIAL_COMPILER_SCRIPT

export CC=REPLACEME_CC
export CXX=REPLACEME_CXX

rm -Rf REPLACEME_SV_TOP_BIN_DIR_GDCM
mkdir -p REPLACEME_SV_TOP_BIN_DIR_GDCM
chmod u+w,a+rx REPLACEME_SV_TOP_BIN_DIR_GDCM

rm -Rf REPLACEME_SV_TOP_BLD_DIR_GDCM
mkdir -p REPLACEME_SV_TOP_BLD_DIR_GDCM
cd REPLACEME_SV_TOP_BLD_DIR_GDCM

REPLACEME_SV_CMAKE_CMD -G REPLACEME_SV_CMAKE_GENERATOR \
  -DGDCM_BUILD_SHARED_LIBS=1 \
  -DGDCM_USE_VTK=0 \
  -DGDCM_WRAP_PYTHON=1 \
  -DGDCM_BUILD_APPLICATIONS=1 \
  -DSWIG_EXECUTABLE=REPLACEME_SV_SWIG_EXECUTABLE \
  -DSWIG_DIR=REPLACEME_SV_SWIG_DIR \
  -DSWIG_VERSION=REPLACEME_SV_SWIG_VERSION \
  -DPYTHON_DEBUG_LIBRARY="" \
  -DPYTHON_EXECUTABLE=REPLACEME_SV_TOP_BIN_DIR_PYTHON/REPLACEME_SV_PYTHON_EXECUTABLE \
  -DPYTHON_INCLUDE_DIR=REPLACEME_SV_TOP_BIN_DIR_PYTHON/REPLACEME_SV_PYTHON_INCLUDE_DIR \
  -DPYTHON_INCLUDE_DIR2=REPLACEME_SV_TOP_BIN_DIR_PYTHON/REPLACEME_SV_PYTHON_INCLUDE_DIR \
  -DPYTHON_LIBRARY=REPLACEME_SV_TOP_BIN_DIR_PYTHON/REPLACEME_SV_PYTHON_LIBRARY \
  -DPYTHON_LIBRARY_DEBUG="" \
  -DCMAKE_INSTALL_PREFIX=REPLACEME_SV_TOP_BIN_DIR_GDCM \
  -DCMAKE_BUILD_TYPE=REPLACEME_SV_CMAKE_BUILD_TYPE \
REPLACEME_SV_TOP_SRC_DIR_GDCM

REPLACEME_SV_MAKE_CMD REPLACEME_SV_GDCM_MAKE_FILENAME REPLACEME_SV_MAKE_BUILD_PARAMETERS

REPLACEME_SV_MAKE_CMD REPLACEME_SV_GDCM_MAKE_FILENAME REPLACEME_SV_MAKE_INSTALL_PARAMETERS



