REPLACEME_SV_SPECIAL_COMPILER_SCRIPT

export CC=REPLACEME_CC
export CXX=REPLACEME_CXX

rm -Rf REPLACEME_SV_TOP_BIN_DIR_HDF5
mkdir -p REPLACEME_SV_TOP_BIN_DIR_HDF5
chmod u+w,a+rx REPLACEME_SV_TOP_BIN_DIR_HDF5

rm -Rf REPLACEME_SV_TOP_BLD_DIR_HDF5
mkdir -p REPLACEME_SV_TOP_BLD_DIR_HDF5
cd REPLACEME_SV_TOP_BLD_DIR_HDF5

REPLACEME_SV_CMAKE_CMD -G REPLACEME_SV_CMAKE_GENERATOR \
  -DHDF5_BUILD_SHARED_LIBS=1 \
  -DHDF5_BUILD_HL_LIB:BOOL=ON \
  -DHDF5_BUILD_CPP_LIB:BOOL=ON \
  -DCMAKE_INSTALL_PREFIX=REPLACEME_SV_TOP_BIN_DIR_HDF5 \
  -DCMAKE_BUILD_TYPE=REPLACEME_SV_CMAKE_BUILD_TYPE \
REPLACEME_SV_TOP_SRC_DIR_HDF5

REPLACEME_SV_MAKE_CMD REPLACEME_SV_HDF5_MAKE_FILENAME REPLACEME_SV_MAKE_BUILD_PARAMETERS

REPLACEME_SV_MAKE_CMD REPLACEME_SV_HDF5_MAKE_FILENAME REPLACEME_SV_MAKE_INSTALL_PARAMETERS

REPLACEME_SV_SPECIAL_COMPILER_END_SCRIPT


