#/bin/bash  -f

REPLACEME_SV_SPECIAL_COMPILER_SCRIPT

export CC=REPLACEME_CC
export CXX=REPLACEME_CXX

rm -Rf  REPLACEME_SV_TOP_BIN_DIR_SWIG
mkdir -p REPLACEME_SV_TOP_BIN_DIR_SWIG
chmod u+w,a+rx REPLACEME_SV_TOP_BIN_DIR_SWIG

cd ../REPLACEME_SV_SWIG_VERSION_DIR
./configure --prefix=REPLACEME_SV_TOP_BIN_DIR_SWIG \
  --without-alllang \
  --with-tclconfig=REPLACEME_SV_TOP_BIN_DIR_TCL/REPLACEME_SV_TCL_DLL_DIR \
  --with-tcl=REPLACEME_SV_TOP_BIN_DIR_TCL       \
  --with-tclincl=REPLACEME_SV_TOP_BIN_DIR_TCL/include  \
  --with-tcllib=REPLACEME_SV_TOP_BIN_DIR_TCL/REPLACEME_SV_TCL_DLL_DIR  \
  --with-python=REPLACEME_SV_TOP_BIN_DIR_PYTHON

REPLACEME_SV_MAKE_CMD REPLACEME_SV_MAKE_BUILD_PARAMETERS
REPLACEME_SV_MAKE_CMD install

cd ../BuildHelpers

REPLACEME_SV_SPECIAL_COMPILER_END_SCRIPT
